INSERT INTO `site_news` VALUES (63, 'معهد البحوث العلمية وإحياء التراث الإسلامي يقيم ورشة عمل عن النشر العلمي الدولي', 'images/workshop.png', 4, '<p><strong><font color="#6600ff" size="5"> </font></strong></p>\r\n<div dir="rtl" style="text-align: center; text-indent: 14.4pt; margin: 0cm 57.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><strong><font color="#6600ff" size="5"><b><span style="line-height: 150%; color: #6600ff; font-size: 16pt"><b><span style="color: #6600ff; font-size: 16pt">معهد البحوث العلمية و إحياء التراث الإسلامي</span></b></span></b></font></strong></div>\r\n<p><strong><font color="#6600ff" size="5"> </font></strong></p>\r\n<div dir="rtl" style="text-align: center; text-indent: 14.4pt; margin: 0cm 57.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><strong><font color="#6600ff" size="5">يقيم ورشة عمل بعنوان</font></strong></div>\r\n<p><strong><font color="#6600ff" size="5">\r\n<div dir="rtl" style="text-align: center; text-indent: 14.4pt; margin: 0cm 57.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center">&nbsp;</div>\r\n<div dir="rtl" style="text-align: center; text-indent: 14.4pt; margin: 0cm 57.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: #6600ff; font-size: 16pt">النشر العلمي الدولي</span></b></div>\r\n<div style="text-align: center; margin: 0cm 7.2pt 0pt 21.6pt" align="center"><b>&nbsp;</b></div>\r\n<p>\r\n<table border="0" cellpadding="0" width="104%" style="width: 104.16%">\r\n    <tbody>\r\n        <tr style="height: 34.85pt">\r\n            <td width="98%" style="border-bottom: #ece9d8; border-left: #ece9d8; padding-bottom: 0.75pt; background-color: transparent; padding-left: 0.75pt; width: 98.6%; padding-right: 0.75pt; height: 34.85pt; border-top: #ece9d8; border-right: #ece9d8; padding-top: 0.75pt">\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: black; font-size: 16pt">برعاية </span></b><b><span style="line-height: 150%; color: black; font-size: 16pt">الأستاذ </span></b><b><span style="line-height: 150%; color: black; font-size: 16pt">الدكتور </span></b><strong><span style="line-height: 150%; font-size: 16pt">عادل بن محمد عسيري </span></strong><b><span style="line-height: 150%; color: black; font-size: 16pt">عميد معهد البحوث العلمية وإحياء التراث الإسلامي</span></b></div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: black; font-size: 16pt">يسر معهد البحوث العلمية وإحياء التراث الإسلامي دعوة أصحاب السعادة أعضاء هيئة التدريس وطلاب الدراسات العليا (رجال) لحضور ورشة العمل التى ينظمها المعهد</span></b><b><span style="line-height: 150%; color: blue; font-size: 16pt">عند الساعة التاسعة صباحاً من &nbsp;</span></b><b><span style="line-height: 150%; color: blue; font-size: 16pt">يومي الثلاثاء والأربعاء </span></b><b><span style="line-height: 150%; color: blue; font-size: 16pt">18 و 19 ذو القعدة الموافق 24 و 25 سبتمبر في معمل الحاسب الآلي (G077-2) بمعهد البحوث العلمية وإحياء التراث بالعابدية</span></b><b><span style="line-height: 150%; color: black; font-size: 16pt"> تحت عنوان/</span></b></div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center">&nbsp;</div>\r\n            <div dir="rtl" style="text-align: center; line-height: normal; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="color: red; font-size: 72pt">النشر العلمي الدولي</span></b></div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center">&nbsp;</div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: #006600; font-size: 16pt">وسيقوم بتقديم هذه الورشة سعادة الدكتور </span></b><strong><span style="line-height: 150%; font-size: 16pt">محمد فوزي رمضان حسانين</span></strong></div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: #006600; font-size: 16pt">الاستاذ المشارك بمعهد البحوث العلمية وإحياء التراث</span></b><b><span style="line-height: 150%; color: #006600; font-size: 16pt">الإسلامي </span></b></div>\r\n            <div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: #006600; font-size: 16pt">و</span></b><b><span style="line-height: 150%; color: #006600; font-size: 16pt">المسئول الأكاديمى عن وحدة النشر الدولى بالمعهد</span></b></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<div dir="rtl" style="text-align: center; margin: 0cm 21.6pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center">&nbsp;</div>\r\n<div dir="rtl" style="text-align: center; margin: 0cm 36pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b><span style="line-height: 150%; color: black; font-size: 16pt">وتهدف هذه الورشة الى العمل على تنمية قدرات أعضاء هيئة التدريس وطلاب الدراسات العليا على نشر أبحاثهم العلمية فى الدوريات العالمية المحكّمة، </span></b><b><span style="line-height: 150%; font-size: 16pt">كما سيقوم المعهد بمنح </span></b><b><span style="line-height: 150%; font-size: 16pt">ال</span></b><b><span style="line-height: 150%; font-size: 16pt">حاضرين شهادات إتمام حضور هذه الورشة.</span></b></div>\r\n<div dir="rtl" style="text-align: center; margin: 0cm 36pt 0pt 7.2pt; unicode-bidi: embed; direction: rtl" align="center"><b>&nbsp;</b></div>\r\n<div dir="rtl" style="text-align: center; margin: 0cm 36pt 0pt 7.2pt