INSERT INTO `site_news` VALUES (78, 'معهد البحوث العلمية وإحياء التراث الإسلامي يهنئ سعادة الدكتور بدر بن أحمد حبيب الله بمناسبة تكليف سعادته مستشاراً خاصاً لمعالي مدير الجامعة', 'images/formal2.png', 2, '<p style="text-align: center;"><img src="/upload/image/Dr_adel.png" width="279" height="349" alt="" />&nbsp;&nbsp;<img src="/upload/image/habib.jpg" width="207" height="229" alt="" />&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><b style="line-height: 200%; text-align: right;"><span lang="AR-SA" style="font-size: 9pt; line-height: 200%; font-family: Tahoma, sans-serif;">يتقدم عميد معهد البحوث العلمية وإحياء التراث الإسلامي أ. د</span></b><b style="line-height: 200%; text-align: right;"><span dir="LTR" style="font-size: 9pt; line-height: 200%; font-family: Tahoma, sans-serif;">.&nbsp;</span></b><b style="line-height: 200%; text-align: right;"><span lang="AR-SA" style="font-size:10.5pt;line-height:200%;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;\r\ncolor:blue">عادل بن محمد عسيري</span></b><b style="line-height: 200%; text-align: right;"><span lang="AR-SA" style="font-size: 9pt; line-height: 200%; font-family: Tahoma, sans-serif;"> بالأصالة عن نفسه ونيابة عن زملائه وكلاء المعهد ومديرو المراكز البحثية والباحثين وجميع منسوبي المعهد بأجمل وأصدق التهاني القلبية لسعادة الدكتور</span></b><b style="line-height: 200%; text-align: right;"><span dir="LTR" style="font-size: 9pt; line-height: 200%; font-family: Tahoma, sans-serif;">&nbsp;</span></b><b style="line-height: 200%; text-align: right;"><span lang="AR-SA" style="font-size:10.5pt;line-height:200%;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;\r\ncolor:blue">بدر بن أحمد حبيب الله </span></b><b style="line-height: 200%; text-align: right;"><span lang="AR-SA" style="font-size: 9pt; line-height: 200%; font-family: Tahoma, sans-serif;">بمناسبة صدور قرار معالي مدير الجامعة بتكليف سعادته مستشاراً خاصاً لمعالي مدير الجامعة</span></b></p>\r\n<p dir="RTL" style="margin: 0cm 3.75pt 0.0001pt; text-align: right; line-height: 2