INSERT INTO `site_news` VALUES (81, 'معهد البحوث العلمية وإحياء التراث الإسلامي (قسم الطالبات) ينظم ورشة عمل لعضوات هيئة التدريس وطالبات الدراسات العليا عن "صياغة وإعداد البحث العلمي: خطة البحث"', 'images/workshop.png', 4, '<p class="MsoNormal" align="center" dir="RTL" style="margin: 0cm 3.4pt 0.0001pt; text-align: center; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size:15.0pt;\r\nline-height:200%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;"><br />\r\n&nbsp;</span></b></p>\r\n<p class="MsoNormal" align="center" dir="RTL" style="margin: 0cm 3.4pt 0.0001pt; text-align: center; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size:15.0pt;\r\nline-height:200%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;">ينظم معهد البحوث العلمية وإحياء التراث الإسلامي (قسم الطالبات) لمدة ثلاثة أيام اعتباراً من يوم الثلاثاء الموافق 2/ 1 / 1435هـ من الساعة 10 صباحاً حتى الساعة الواحدة ظهراً ورشة عمل لعضوات هيئة التدريس وطالبات الدراسات العليا بعنوان &quot; صياغة وإعداد البحث العلمي: خطة البحث&quot; تقدمها الدكتورة</span></b><b><span dir="LTR" style="font-size:15.0pt;mso-bidi-font-size:11.0pt;line-height:200%;\r\nfont-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;">&nbsp;</span></b><b><span lang="AR-SA" style="font-size:15.0pt;\r\nline-height:200%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;">نيفين بنت حمزة البركاتي </span></b><b><span dir="LTR" style="font-size:15.0pt;mso-bidi-font-size:11.0pt;line-height:200%;font-family:\r\n&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;">&nbsp;</span></b><b><span lang="AR-SA" style="font-size:15.0pt;line-height:200%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;">وذلك بقاعة الجفالي (2) بمبنى ( ب ) الدور الثاني بمقر عمادة الدراسات الجامعية للطالبات بحي الزاهر</span></b><b><span dir="LTR" style="font-size:15.0pt;line-height:200%;\r\nfont-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;">.</span></b><span dir="LTR" style="font-size:9.5pt;line-height:\r\n200%;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;"><o:p></o:p></span></p>\r\n<p class="MsoNormal" align="center" dir="RTL" style="margin: 0cm 3.4pt 0.0001pt; text-align: center; line-height: 37.5pt; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size:15.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;">ودعا المعهد الراغبات في حضور هذه الورشة إلى التسجيل وذلك بمراجعة وكالة معهد البحوث العلمية وإحياء التراث الإسلامي مبنى (ب) الدور الثالث لدى الأستاذة أميرة السريحي أو الاتصال على رقم الهاتف5426222 تحويلة 7732</span></b><span dir="LTR" style="font-size:9.5pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;"><o:p></o:p></span></p>\r\n<p class="MsoNormal" align="center" dir="RTL" style="text-align:center"><span dir="LTR"><o:p>&nbsp;</o:p></span></p>', '2013-10-24', '2013-11-06', 1, 1, '2', '', '0000-00-00 00:00:00', 20098);
INSERT INTO `site_news` VALUES (82, 'جامعة أم القرى تشارك في معرض فرانكفورت الدولي للكتاب 2013', 'images/show.png', 3, '<p class="MsoNormal" dir="RTL" style="text-align: center; line-height: 200%;"><img src="/upload/image/Subukshi.jpg" width="612" height="816" alt="" /></p>\r\n<p class="MsoNormal" dir="RTL" style="text-align: center; line-height: 200%;">&nbsp;&nbsp;<img src="/upload/image/Subukshi3(1).jpg" width="350" height="467" alt="" />&nbsp;&nbsp;</p>\r\n<p class="MsoNormal" dir="RTL" style="text-align: center; line-height: 200%;"><img src="/upload/image/Subukshi4.jpg" width="350" height="467" alt="" /></p>\r\n<p class="MsoNormal" dir="RTL" style="text-align: center; line-height: 200%;"><img src="/upload/image/Subukshi5.jpg" width="800" height="611" alt="" /></p>\r\n<p class="MsoNormal" dir="RTL" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:\r\nauto;text-align:justify;text-justify:inter-ideograph;line-height:200%"><b><span lang="AR-SA" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;"><br />\r\nشاركت جامعة أم القرى ممثلة في معهد البحوث العلمية وإحياء التراث الإسلامي في معرض فرانكفورت الدولي للكتاب لعام2013م في دورته الخامسة والستين الذي افتتح يوم الاربعاء 9/أكتوبر/2013 الموافق 4/ذو الحجة/1434هـ والذي يعد من أضخم معارض الكتاب الدولية وأهمها في سوق النشر العالمية، حيث ضم المعرض ست قاعات ضخمة استضافت المشاركين من أكثر من (118) دولة، وقدِّر عدد زواره بـثلاثمائة الف شخص وحضي بتغطية إعلامية واسعة .<o:p></o:p></span></b></p>\r\n<p class="news" dir="RTL" style="margin: 2.7pt 0cm; text-align: justify; line-height: 200%; vertical-align: top; direction: rtl; unicode-bidi: embed; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;">هذا وقد افتتح المعرض وزير الخارجية الألماني غيدو فيستر فيله</span></b><b><span dir="LTR" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;">&nbsp;</span></b><b><span style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;"> <span lang="AR-SA">ووزير الثقافة البرازيلي وعمدة فرانكفورت ورئيس اتحاد الناشرين الألمان، فيما افتتح معالي سفير خادم الحرمين الشريفين لدى المانيا الأستاذ الدكتور/ أسامة بن عبدالمجيد شبكشي جناح المملكة العربيَّة السعوديَّة وذلك بحضور كل من سمو حاكم الشارقة الشيخ صقر بن محمد القاسمي وسعادة الدكتور عبدالرحمن بن حمد الحميضي الملحق الثقافي في جمهورية ألمانيا الاتحادية وسعادة الدكتور سالم المالك المستشار والمشرف العام على الإدارة العامَّة للتعاون الدولي بوزارة التَّعليم العالي وسعادة الدكتور علي بن صقر الملحق الثقافي بجمهورية النمسا والدكتور سليمان بن صالح العقلا مستشار وزارة التَّعليم العالي وعدد كبير من الأدباء والمثقفين وممثلي الجهات الحكوميَّة والخاصَّة المشاركة بالجناح ومجموعة من الطلبة المبتعثين والزائرين، وقد شغل جناح المملكة مكانًا بارزًا في المعرض على مساحة تجاوزت المائة وعشرين مترًا مربعًا حرصت الملحقية الثقافية بجمهورية ألمانيا الاتحادية بوصفها الجهة المشرفة على الجناح فيه على أن يتواءم مع المناسبة بما يعكس الأنشطة الثقافية السعوديَّة باختلاف قنواتها في مجالات النشر والفنون والتراث؛ حيث خصص لمجال النشر مساحة واسعة لقسم معني بالإصدارات في الأدب، والعلوم، واللغة العربيَّة، والتاريخ، والجغرافيا، والكتب العلميَّة بالإضافة إلى إصدارات المناسبة التي تشكّل مجموعة من الكتب المنتقاة التي تمَّت ترجمتها إلى اللغات الحية كالألمانية والإنجليزية وتركز على الموضوعات التي تهم القارئ في ألمانيا وتبرز القواسم المشتركة بين الثقافة السعوديَّة والألمانية.<o:p></o:p></span></span></b></p>\r\n<p class="MsoNormal" dir="RTL" style="margin: 0cm 2.7pt 0.0001pt; text-align: justify; line-height: 200%; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;">وقد مثل جامعة أم القرى في هذه المناسبة العالمية كل من رئيس الوفد سعادة الأستاذ الدكتور صالح بن منيع الحربي عضو مجلس المعهد، وسعادة المهندس الأستاذ فؤاد بن عبد الله العطاس المشرف العام على وحدة الحاسب الآلي وتقنية المعلومات بالمعهد، حيث قاموا بالإجابة عن استفسارات الزوار وتحقيق متطلباتهم.</span></b><b><span dir="LTR" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;"><o:p></o:p></span></b></p>\r\n<p class="MsoNormal" dir="RTL" style="margin: 0cm 2.7pt 0.0001pt; text-align: justify; line-height: 200%; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;">&nbsp;<o:p></o:p></span></b></p>\r\n<p class="MsoNormal" dir="RTL" style="margin: 0cm 2.7pt 0.0001pt; text-align: justify; line-height: 200%; background-position: initial initial; background-repeat: initial initial;"><b><span lang="AR-SA" style="font-size: 18pt; line-height: 200%; font-family: Arial, sans-serif;">وقد عبر سعادة الأستاذ الدكتور عادل بن محمد عسيري عميد معهد البحوث العلمية وإحياء التراث الإسلامي عن عظيم شكره وتقديره لمعالي مدير الجامعة الدكتور بكري بن معتوق عساس لدعمه المتواصل وتشجيعه اللامحدود لإظهار مشاركات جامعة أم القرى في مختلف المحافل الدولية بالمظهر الذي يليق بمكانة وسمعة هذه الجامعة العريقة التي تتشرف بوجودها على أطهر ثرى على وجه الأرض.<o:p></o:p></span></b></p>', '2013-10-23', '2013-10-31', 1, 0, '2', '2', '0000-00-00 00:00:00', 20101);
INSERT INTO `site_news` VALUES (83, 'جامعة أم القرى تشارك في معرض تونس الدولي للكتاب في دورته الثلاثين لعام 2013م', 'images/show.png', 3, '<p style="text-align: center;"><img src="/upload/image/photo%202.JPG" width="650" height="487" alt="" /></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="/upload/image/photo%201.jpg" width="650" height="493" alt="" /></p>\r\n<p>&nbsp;</p>\r\n<p dir="RTL" style="text-align:justify;line-height:37.5pt;direction:rtl;\r\nunicode-bidi:embed"><b><span lang="AR-SA" style="font-size:18.0pt;font-family:\r\n&quot;Arial&quot;,&quot;sans-serif&quot;;mso-ascii-theme-font:minor-bidi;mso-hansi-theme-font:minor-bidi;\r\nmso-bidi-theme-font:minor-bidi">شاركت جامعة أم القرى ممثلة في معهد البحوث العلمية وإحياء التراث الإسلامي في معرض تونس الدولي للكتاب لعام 2013م في دورته الثلاثين الذي افتتح يوم الجمعة 20/12/1434هـ،&nbsp; بحضور معالي وزير الثقافة التونسي السيد المهدي مبروك ومعالي سفير خادم الحرمين الشريفين لدى الجمهورية التونسية <span style="background-position: initial initial; background-repeat: initial initial;">الاستاذ خالد بن مساعد العنقري وسعادة الملحق الثقافي السعودي الدكتور أيمن بن عبدالرحمن مغربي وممثلي الجهات الرسمية للدول العربية وعدد من أبناء الجاليات العربية المقيمة في تونس. </span></span></b><b><span dir="LTR" style="font-size:18.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-ascii-theme-font:\r\nminor-bidi;mso-hansi-theme-font:minor-bidi;mso-bidi-theme-font:minor-bidi"><o:p></o:p></span></b></p>\r\n<p dir="RTL" style="text-align:justify;line-height:37.5pt;direction:rtl;\r\nunicode-bidi:embed"><b><span lang="AR-SA" style="font-size:18.0pt;font-family:\r\n&quot;Arial&quot;,&quot;sans-serif&quot;;mso-ascii-theme-font:minor-bidi;mso-hansi-theme-font:minor-bidi;\r\nmso-bidi-theme-font:minor-bidi">وقد مثل الجامعة في هذا المعرض كل من سعادة الدكتور عبدالرحمن بن غالب الأهدل (مدير مركز بحوث العلوم الهندسية والمعمارية بمعهد البحوث العلمية وإحياء التراث الإسلامي) رئيساً للوفد بالإضافة إلى كل من الأستاذ حمدي بن حمد الحتيرشي الموظف بالمعهد والأستاذ فهد بن عبدالله المجيرشي من عمادة شئون المكتبات، حيث أوضح سعادة رئيس أن جناح المملكة العربية السعودية قد شغل مكانًا بارزًا في المعرض نتيجة حرص واهتمام الملحقية الثقافية بتونس بوصفها الجهة المشرفة على الجناح على أن يتواءم الجناح السعودي مع المناسبة بما يعكس الأنشطة الثقافية والعلمية في المملكة العربية السعوديَّة.<o:p></o:p></span></b></p>\r\n<p dir="RTL" style="text-align:justify;line-height:37.5pt;direction:rtl;\r\nunicode-bidi:embed"><b><span lang="AR-SA" style="font-size: 18pt; font-family: Arial, sans-serif; background-position: initial initial; background-repeat: initial initial;">هذا وقد قام معالي وزير الثقافة التونسي بجولة داخل جناح جامعة أم القرى، اطلع خلالها على المطبوعات المعروضة من كتب ودوريات ورسائل علمية وأبحاث محققة، كما تجول معاليه في جناح المملكة وشاهد معرض تطور مؤسسات التعليم العالي ومعرض يوضح اهتمام حكومة خادم الحرمين الشريفين حفظه الله بتطوير مكة المكرمة والمدينة المنورة والمشاعر المقدسة مبدياً إعجابه الشديد بما شاهده وما لمسه من إقبال الجماهير على اقتناء الكتاب السعودي والأبحاث السعودية والتي تدل دلالة واضحة على ما تشهده الحركة العلمية والثقافية في المملكة من تطور هائل مبدياً شكره وتقديره لما بذل من جهود وما حظي به معاليه وزوار المعرض من حسن استقبال وترحاب</span></b><b><span dir="LTR" style="font-size: 18pt; font-family: Arial, sans-serif; background-position: initial initial; background-repeat: initial initial;">. </span></b><b><span lang="AR-SA" style="font-size:18.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\nmso-ascii-theme-font:minor-bidi;mso-hansi-theme-font:minor-bidi;mso-bidi-theme-font:\r\nminor-bidi"><o:p></o:p></span></b></p>\r\n<p dir="RTL" style="text-align:justify;line-height:37.5pt;direction:rtl;\r\nunicode-bidi:embed"><b><span lang="AR-SA" style="font-size:18.0pt;font-family:\r\n&quot;Arial&quot;,&quot;sans-serif&quot;;mso-ascii-theme-font:minor-bidi;mso-hansi-theme-font:minor-bidi;\r\nmso-bidi-theme-font:minor-bidi">وأعرب سعادة عميد معهد البحوث العلمية وإحياء التراث الإسلامي الأستاذ الدكتور عادل بن محمد عسيري وأعضاء الوفد الممثل للجامعة عن وافر شكرهم و كامل تقديرهم لمعالي مدير الجامعة الدكتور بكري بن معتوق عساس لدعمه المتواصل وتشجيعه غير المحدود لإظهار مشاركات جامعة أم القرى في مختلف المحافل الدولية بالمظهر الذي يليق بمكانة وسمعة هذه الجامعة العريقة</span></b><b><span dir="LTR" style="font-size:18.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\nmso-ascii-theme-font:minor-bidi;mso-hansi-theme-font:minor-bidi;mso-bidi-theme-font:\r\nminor-bidi">.&nbsp;</span></b><b><span lang="AR-SA" style="font-size:18.0pt;mso-ascii-font-family:Arial;mso-ascii-theme-font:minor-bidi;\r\nmso-hansi-font-family:Arial;mso-hansi-theme-font:minor-bidi"><o:p></o:p></span></b></p>', '2013-10-28', '2013-11-30', 1, 1, '2', '2', '0000-00-00 00:00:00', 20140);
INSERT INTO `site_news` VALUES (84, 'جامعة أم القرى تشارك في اجتماع وكلاء الجامعات للدراسات العليا وعمداء البحث العلمي المنعقدة في مدينة الملك عبدالعزيز للعلوم والتقنية', 'images/formal1.png', 1, '<p style="text-align: center;"><img src="/upload/image/dr%20hani.jpg" width="250" height="473" alt="" />&nbsp; &nbsp;&nbsp;<img src="/upload/image/Dr_adel.png" width="279" height="349" alt="" /></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="/upload/image/Kacst.jpg" width="500" height="586" alt="" /></p>\r\n<p>&nbsp;</p>\r\n<p class="MsoNormal" dir="RTL" style="text-align:justify;text-indent:0cm;\r\nline-height:200%"><b><span lang="AR-SA" style="font-size:36.0pt;line-height:200%;\r\nfont-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-ascii-font-family:Calibri;mso-ascii-theme-font:\r\nminor-latin;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-font-family:Arial;mso-bidi-theme-font:minor-bidi">شارك كل من سعادة وكيل الجامعة للدراسات العليا والبحث العلمي الدكتور هاني بن عثمان غازي وسعادة عميد معهد البحوث العلمية وإحياء التراث الإسلامي الأستاذ الدكتور عادل بن محمد عسيري في الاجتماع الذي عقد في مدينة الملك عبدالعزيز للعلوم والتقنية بالرياض يوم الثلاثاء 24 ذو الحجة 1434هـ الموافق 29 أكتوبر 2013م كممثلين لجامعة أم القرى والذي ضم وكلاء الجامعات السعودية للدراسات العليا والبحث العلمي وكذلك عمداء البحث العلمي لمناقشة متطلبات وسبل تطوير البحث العلمي في المملكة العربية السعودية، وقد تناول اللقاء مناقشة عدة قضايا تهم البحث العلمي مثل مبادرات دعم البحث العلمي، ومؤشرات أداء البحث العلمي وغيرها من القضايا المدرجة على جدول أعمال اللقاء بالإضافة إلى الموضوعات المقترحة من وكلاء الجامعات للدراسات العليا والبحث العلمي وعمداء البحث العلمي. <o:p></o:p></span></b></p>\r\n<p class="MsoNormal" dir="RTL" style="text-align:justify;text-indent:0cm;\r\nline-height:200%"><b><span lang="EN-GB" dir="LTR" style="font-size:36.0pt;\r\nline-height:200%"><o:p>&nbsp;</o:p></span></b></p>', '2013-10-30', '2013-11-30', 1, 1, '2', '', '0000-00-00 00:00:00', 20167);
